mapper.update:
update busi_exhibition
set
    status = 4,
    update_time = now(),
    update_by = #{updateBy}
where exhibition_id = #{exhibitionId}

mapper.select:
select send_id
from busi_send
where exhibition_id = #{exhibitionId}

mapper.insert:
INSERT INTO  busi_revoke  (  revoke_id ,  exhibition_id ,  revoke_url ,  file_name ,  create_by ,  create_time )
VALUES
( #{revokeId}, #{exhibitionId}, #{revokeUrl}, #{fileName}, #{createBy}, now() );

mapper.select:
select revoke_url
from busi_revoke
where revoke_id = #{revokeId}
